    public static List<Integer> findSubstring(String s, String[] words) {
        LinkedList<Integer> res = new LinkedList<>();
        Set<String> set = new HashSet<>();
        if(s==""||"".equals(s)){
            return Collections.EMPTY_LIST;
        }
        char[] chars = s.toCharArray();
        int first = -1;
        Arrays.sort(words);
        for(int i=0;i<chars.length-words.length*words[0].length()+1;i++){
            set.clear();
            List<String> wordList = new ArrayList(Arrays.asList(words));
            for(int k=0;k<wordList.size();k++){
                String stmp = wordList.get(k);
                if(set.contains(stmp)){
                    continue;
                }
                for(int j=0;j<stmp.length();j++){
                    if(i+j>=chars.length){
                        break;
                    }
                    if(chars[i+j]!=stmp.charAt(j)){
                        set.add(stmp);
                        break;
                    }
                    if(j==stmp.length()-1){
                        if(wordList.size()==words.length){
                            first = i;
                        }
                        if(wordList.size()==1){
//                            System.out.println(first);
                            res.add(first);
                            break;
                        }
                        i=i+stmp.length();
                        wordList.remove(k);
                        k = -1; //找到一个匹配的就重置为-1
                        set.clear();
                        break;
                    }
                }
            }
            if(first>=0){
                i = first;
            }
            first = -1;
        }
        return res;
    }
